import "./contact.css";
//import icons
import { useRef, useState } from "react";
import emailjs from '@emailjs/browser';

const Contact=()=>{
  const formRef= useRef()
  const [done, setDone]=useState(false)
  const handleSubmit=(e)=> {
    e.preventDefault();
    emailjs
      .sendForm(
        'service_hrkqvwz',
        'template_jznto9l',
        formRef.current,
        'HDe5nyLCaKGs1Nf-H')

      .then(
        (result) => {
          console.log(result.text);
          setDone()
        },
          (error) => {
            console.log(error.text);
        });
  }

  return (
    <div className="c">
      <div className="c-bg"></div>
        <div className="c-wrapper">
          <div className="c-left">
            <h1 className="c-title">let's talk about my projects</h1>
            <div className="c-info">
              <div className="c-info-item">
                <img src="" alt="phone-icon" className="c-icon" />
                +49 1753486797
              </div>
              <div className="c-info-item">
                <img src="" alt="email-icon" className="c-icon" />
                marlowzhao@163.com
              </div>
              <div className="c-info-item">
                <img src="" alt="address-icon" className="c-icon" />
                Leonorenstrasse 36A Berlin
              </div>
            </div>

          </div>`

          <div className="c-right">
            <p className="c-desc">
              <b>seek help? </b>
              <b>let's talk about possibilities for further cooperation</b>
            </p>
              <form ref={formRef} onSubmit={handleSubmit}>
                <input type="text" placeholder="Name" name="user_name"/>
                <input type="text" placeholder="Subject" name="user_subject"/>
                <input type="text" placeholder="Email" name="user_email"/>
                <textarea rows="5" placeholder="Message" name="message"></textarea>
                <button>Submit</button>
              </form>
          </div>
        </div>
    </div>
  )
}

export default Contact
